// Updated Features Component with proper icon usage
const Technology = () => {
  const technologies = [
    {
      name: 'Frontend: React',
      icon: <ReactIcon size={64} />,
      color: '#61DAFB'
    },
    {
      name: 'Backend: Django',
      icon: <DjangoIcon size={64} />,
      color: '#092E20'
    },
    {
      name: 'Database: PostgreSQL/PostGIS',
      icon: <PostgreSQLIcon size={64} />,
      color: '#336791'
    },
    {
      name: 'Mapping: Mapbox/Leaflet',
      icon: <MapIcon size={64} />,
      color: '#4264FB'
    },
    {
      name: 'Sensors: NPK, Temperature',
      icon: <SensorIcon size={64} />,
      color: '#FF6B35'
    }
  ];

  return (
    <section className='bgclr margincl' id="technology" style={{ padding: '80px 20px' }}>
      <div  className='bgclr' style={{ maxWidth: '1200px', margin: '0 auto' }}>
        <div style={{ textAlign: 'center', marginBottom: '60px' }}>
          <h1 className="team-sec text-[42px]  font-bold font-head text-primary-100 lg:text-6xl">
              Powered by Modern Technology
      </h1>
          <p style={{ fontSize: '1.25rem', color: '#6c757d' }}>
            Built on a robust, scalable stack to deliver real-time data and powerful insights.
          </p>
        </div>
        
        <div style={{ 
          overflow: 'hidden',
          position: 'relative',
          padding: '20px 0'
        }}>
          <div style={{
            display: 'flex',
            animation: 'scroll 30s linear infinite',
            gap: '60px'
          }}>
            {/* First set of items */}
            {technologies.map((tech, index) => (
              <div key={`tech-1-${index}`} style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                gap: '15px',
                minWidth: '200px',
                padding: '20px'
              }}>
                <div style={{ color: tech.color }}>
                  {tech.icon}
                </div>
                <p style={{ fontWeight: '600', margin: 0, textAlign: 'center' }}>
                  {tech.name}
                </p>
              </div>
            ))}
            {/* Duplicate set for seamless loop */}
            {technologies.map((tech, index) => (
              <div key={`tech-2-${index}`} style={{
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                gap: '15px',
                minWidth: '200px',
                padding: '20px'
              }}>
                <div style={{ color: tech.color }}>
                  {tech.icon}
                </div>
                <p style={{ fontWeight: '600', margin: 0, textAlign: 'center' }}>
                  {tech.name}
                </p>
              </div>
            ))}
          </div>
        </div>
      </div>

      <style>{`
        @keyframes scroll {
          0% {
            transform: translateX(0);
          }
          100% {
            transform: translateX(-50%);
          }
        }
      `}</style>
    </section>
  );
};

export default Technology;